import React from "react";
import { useSelector } from "react-redux";

const Navbar = () => {
  const state = useSelector((state) => state.cart);
  var total = 0;
  const openCart = () => {
    document.querySelector(".effort").style.display = "flex";
  };
  function closeCart() {
    document.querySelector(".effort").style.display = "none";
  }
  return (
    <div>
      <div className="echo">
        <div>
          <i>
            <svg
              id="shopping_cart_black_24dp"
              xmlns="http://www.w3.org/2000/svg"
              width="24"
              height="24"
              viewBox="0 0 24 24"
              onClick={openCart}
            >
              <path
                id="Path_17357"
                data-name="Path 17357"
                d="M0,0H24V24H0Z"
                fill="none"
              ></path>
              <path
                id="Path_17358"
                data-name="Path 17358"
                d="M15.55,13a1.991,1.991,0,0,0,1.75-1.03l3.58-6.49A1,1,0,0,0,20.01,4H5.21L4.27,2H1V4H3l3.6,7.59L5.25,14.03A2,2,0,0,0,7,17H19V15H7l1.1-2ZM6.16,6H18.31l-2.76,5H8.53ZM7,18a2,2,0,1,0,2,2A2,2,0,0,0,7,18Zm10,0a2,2,0,1,0,2,2A2,2,0,0,0,17,18Z"
                fill="#00aeef"
              ></path>
            </svg>
          </i>
          <span className="educator">{state.length}</span>
        </div>
        <div>Cart</div>
      </div>

      {/* cart pop-up */}
      <div className="effort">
        <div className="faith">
          <div className="fish" onClick={closeCart}>
            {" "}
            +
          </div>
          <h3>Order Summary</h3>
          {state.map((item) => {
            total = total + Number(item.price);
            return (
              <div className="follower">
                <div>
                  <img src={item.pImg} alt={item.name} className="flock" />
                </div>

                <div>
                  <p className="hat">{item.name}</p>
                  <div className="hall">
                    <div>₹{item.price}</div>
                    <div className="heat">
                      <button>-</button>
                      {item.qty}
                      <button>+</button>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
          <hr></hr>
          <h3>Price Summary</h3>
          <hr style={{ width: "95%" }}></hr>
          <div className="hell">
            <div>Order Total</div>
            <div>₹{total}</div>
          </div>
          <hr style={{ width: "95%" }}></hr>
          <div className="hell">
            <div>Shipping</div>
            <div style={{ color: "green" }}>Free</div>
          </div>
          <hr style={{ width: "95%" }}></hr>
          <div className="hell">
            <div>5% online payment discount</div>
            <div style={{ color: "green" }}>-₹{0.05*total}</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Navbar;
